SET NAMES 'utf8';
DROP DATABASE IF EXISTS miyou;
CREATE DATABASE miyou CHARSET=UTF8;
USE miyou;


/*人气精选目的地表*/
CREATE TABLE my_destination(
    did INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(64),
    discount FLOAT(3,2),
    price FLOAT(8,2),
    pic VARCHAR(64)
);


/*热门自由行表*/
CREATE TABLE my_free(
    fid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(2048),
    destination VARCHAR(512),
    price FLOAT(8,2),
    disprice FLOAT(8,2),
    buyed INT,
    pic VARCHAR(256),
    detail VARCHAR(8192)

);



/*更多好玩表*/
CREATE TABLE my_hot(
    hid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(2048),
    destination VARCHAR(512),
    vprice VARCHAR(256),
    disprice VARCHAR(256),
    price FLOAT(8,2),
    buyed INT,
    pic VARCHAR(256)
);


/*更多好玩详情表*/
CREATE TABLE my_detail(
    eid INT PRIMARY KEY AUTO_INCREMENT,
    subtitle TEXT(12288),
    text1 VARCHAR(512),
    ptitle TEXT(12288),
    text2 VARCHAR(512),
    text3 VARCHAR(512),
    text4 VARCHAR(512),
    hotId int,
    pic1 VARCHAR(256),
    pic2 VARCHAR(256),
    pic3 VARCHAR(256),
    pic4 VARCHAR(256)

);


/*我的订单表*/
CREATE TABLE my_order(
    oid INT PRIMARY KEY AUTO_INCREMENT,
    orderNum BIGINT,
    order_time LONG,
    count int,
    hid INT
);



